# Parse command line arguments

if isempty(ARGS)
    println("Usage: julia nctssos_bell_template.jl <instance> [order]")
    println("  instance: Bell inequality name (e.g., A2, A17, A89)")
    println("  order:    Optional relaxation order (default: from table)")
    println()
    println("Available instances: $(join(BELL_INSTANCES[1:10], ", ")), ..., $(BELL_INSTANCES[end])")
    exit(1)
end

instance = ARGS[1]
idx = findfirst(x -> x == instance, BELL_INSTANCES)

if idx === nothing
    println("Error: Unknown instance '$instance'")
    println("Available: $(join(BELL_INSTANCES, ", "))")
    exit(1)
end

order = length(ARGS) >= 2 ? parse(Int, ARGS[2]) : BELL_ORDERS[idx]
m = BELL_MS[idx]
n = BELL_NS[idx]
equation = BELL_EQUATIONS[idx]
expected = BELL_EXPECTED[idx]

# Run oracle

print_header("Bell Inequality: $instance")

println("# Variables: X[1:$m], Y[1:$n] (projector, P²=P)")
println("# Order: $order")
println("# Expected: $expected")
println()
println("# Equation: $equation")
println()

# Create projector variables (P²=P constraint via constraint="projection")
# In NCTSSOS, Bell inequalities use projection constraint
@ncpolyvar X[1:m] Y[1:n]

# Parse equation and build objective
# This replicates the parsing logic from NCTSSoS.jl tests
function parse_bell_eq(eq_str::String, X::Vector, Y::Vector)
    result = 0.0
    s = eq_str
    if !startswith(s, '+') && !startswith(s, '-')
        s = "+" * s
    end

    term_regex = r"([+-])\s*(\d+)\*([XY]\[\d+\])(?:\*([XY]\[\d+\]))?"
    for m in eachmatch(term_regex, s)
        sign = m.captures[1] == "-" ? -1.0 : 1.0
        coeff = parse(Float64, m.captures[2]) * sign
        var1_str = m.captures[3]
        var2_str = m.captures[4]

        var1_match = match(r"([XY])\[(\d+)\]", var1_str)
        var1_name = var1_match.captures[1]
        var1_idx = parse(Int, var1_match.captures[2])
        var1 = var1_name == "X" ? X[var1_idx] : Y[var1_idx]

        if var2_str === nothing
            result += coeff * var1
        else
            var2_match = match(r"([XY])\[(\d+)\]", var2_str)
            var2_name = var2_match.captures[1]
            var2_idx = parse(Int, var2_match.captures[2])
            var2 = var2_name == "X" ? X[var2_idx] : Y[var2_idx]
            result += coeff * var1 * var2
        end
    end
    result
end

obj = parse_bell_eq(equation, X, Y)
vars = vcat(X, Y)

# Run NCTSSOS with projection constraint
key = "Bell_$(instance)_d$(order)"
println("# Running NCTSSOS...")

opt, data = nctssos_first([obj], vars, order; constraint="projection")
result = extract_oracle(key, opt, data; use_cs=false)

println()
println("# Result:")
print_oracle(result)
println()
println("# Expected optimal: $expected")
println("# Actual optimal:   $(result.opt)")
println("# Difference:       $(abs(result.opt - expected))")

if abs(result.opt - expected) < 1e-4
    println("# Status: MATCH ✓")
else
    println("# Status: MISMATCH - investigate further")
end
